import logo from './logo.svg';
import './App.css';
import './styles.css'
import React, { Component } from 'react';

class EmojiVoting extends Component {
  constructor(props) {
    super(props);
    this.state = {
      votes: {
        emoji1: parseInt(localStorage.getItem('emoji1')) || 0,
        emoji2: parseInt(localStorage.getItem('emoji2')) || 0,
        emoji3: parseInt(localStorage.getItem('emoji3')) || 0,
      },
      winner: null,
    };
  }

  vote = (emoji) => {
    this.setState((prevState) => {
      const newVotes = { ...prevState.votes, [emoji]: prevState.votes[emoji] + 1 };
      localStorage.setItem(emoji, newVotes[emoji]);
      return { votes: newVotes };
    });
  };

  showResults = () => {
    const { votes } = this.state;
    let maxVotes = 0;
    let winner = '';

    Object.entries(votes).forEach(([emoji, count]) => {
      if (count > maxVotes) {
        maxVotes = count;
        winner = emoji;
      }
    });

    this.setState({ winner: `${winner} (${maxVotes} –≥–æ–ª–æ—Å—ñ–≤)` });
  };

  clearResults = () => {
    const resetVotes = {
      emoji1: 0,
      emoji2: 0,
      emoji3: 0,
    };

    localStorage.clear();
    this.setState({ votes: resetVotes, winner: null });
  };

  render() {
    const { votes, winner } = this.state;

    return (
      <div>
        <h1>–ì–æ–ª–æ—Å—É–≤–∞–Ω–Ω—è –∑–∞ –Ω–∞–π–∫—Ä–∞—â–∏–π —Å–º–∞–π–ª–∏–∫</h1>
        <div className='flex wrapper'>
        <div className='flex col'>
          <span>üòÄ</span> 
          <div>
          <button onClick={() => this.vote('emoji1')} className='btn'>Vote</button> 
          {votes.emoji1}
          </div>
        </div>
        <div className='flex col'>
          <span>üòä</span> 
          <div>
          <button onClick={() => this.vote('emoji2')} className='btn'>Vote</button> 
          {votes.emoji2}
          </div>
        </div>
        <div className='flex col'>
          <span>üòé</span> 
          <div>
          <button onClick={() => this.vote('emoji3')}>Vote</button> 
          {votes.emoji3}
          </div>
        </div>
        </div>

        <button onClick={this.showResults}>Show Results</button>
        <p>{winner && `–ü–µ—Ä–µ–º–æ–∂–µ—Ü—å: ${winner}`}</p>

        <button onClick={this.clearResults}>–û—á–∏—Å—Ç–∏—Ç–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∏</button>
      </div>
    );
  }
}

export default EmojiVoting;
